import org.apache.commons.codec.binary.Base64

def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def holder = groovyUtils.getXmlHolder( messageExchange.responseContent )
holder.namespaces["ns1"] = "urn:ihe:iti:xds-b:2007"
def textBase64 = holder["//ns1:RetrieveDocumentSetResponse[1]/ns1:DocumentResponse[1]/ns1:Document[1]"]

def b64 = new Base64()
def DataBytes = b64.decode(textBase64.getBytes())
def output = new String (DataBytes);

def outputHolder = groovyUtils.getXmlHolder( output )
outputHolder.namespaces["ns1"] = "urn:hl7-org:v3"

def countPatientRole = outputHolder["count(//ns1:ClinicalDocument[1]/ns1:recordTarget[1]/ns1:patientRole)"].toInteger()


log.info countPatientRole

assert countPatientRole >0

